import React, { useState } from 'react'
import './FilmList.css'
import Films from '../FilmsCard/Films.js'
import PaginationCompo from '../Pagination/Pagination.js'
import Cover from '../Cover/Cover.js'

const FilmList = ({option, setOption, linkImg, setLanguage , setPage , totalPageNumber}) => {
  const [lang, setLang] = useState('')

  const handelChange = (event) => {
    const lng = event.target.value
    setLanguage(lng)
    setLang(lng)
  }

// reedux  
  const dispatch = useDispatch();
  
  useEffect(() => {
    dispatch(getAllMovie())
  }, [option, language, page]);

  const dataMovies = useSelector((state)=>state.movies)
  
  useEffect(()=>{
    setItems(dataMovies)
    
  },[dataMovies])

  return (
    <div className="container">
      
      {/* Section des boutons et du menu déroulant */}
      <div className="d-flex justify-content-between align-items-center mb-4 option">
        <div>
          <button className='btn bnt me-2' onClick={() => setOption('movie')}>Movie</button>
          <button className='btn bnt' onClick={() => setOption('tv')}>Tv</button>
        </div>
        {option === "movie" && (
          <select className="form-select w-auto" value={lang} onChange={handelChange}>
            <option value="" disabled>Open this select menu</option>
            <option value='ar'>Arabic</option>
            <option value='en'>English</option>
            <option value='fr'>French</option>
          </select>
        )}
      </div>
      
      {/* <Cover /> */}


      {/* Section des films */}
      <div className="row">
        {items.length > 0 ? (
          items.map((item) => {
            return (
              <div className='col-lg-2 col-md-3 col-sm-4 ' key={item.id}>
                <Films linkImg={linkImg} item={item} />
              </div>
            )
          })
        ) : (
          <h3>No data found ♥♪☺</h3>
        )}
        <PaginationCompo setPage={setPage}  totalPageNumber={totalPageNumber} />
      </div>
    </div>
  )
}

export default FilmList
