import {AllMovies , Search} from '../types/moviesType'

const initialValue = {movies : [] , pageCount : 0}
// 2-Create reducer
export const moviesReducer = (state = initialValue , action)=>{
    switch(action.type){
        case AllMovies :
            return { movies : action.data , pageCount :  action.pages }

        case Search :
            return    
            
        default :
            return {state}    
    }

}
