import React from 'react'
import './FilmDetail.css'
import { Link , useParams} from 'react-router-dom'


const FilmDetail = ({item , linkImg}) => {
    const params = useParams() ;
    console.log();
    
  return (
    <div className='container grid-container mt-3'>
        <div className='grid-item-cover'>
            <img src={linkImg + item.homepage} alt='homepage'/>
        </div>
        <div className='grid-item-detaill'>
            <p className="card-text-details border-bottom title">
              {item.title}
            </p>
            <p className="card-text-details border-bottom">
                {item.release_date}
            </p>
            <p className="card-text-details border-bottom">
            language : {item.original_language}
            </p>
            <p className="card-text-details border-bottom">
                MetaScore : {item.vote_average}
            </p>
        </div>

    <Link to='/'>
        <button className='btn btn-success'>Back home</button>
    </Link>
        <button className='btn btn-success'>Watch film</button>
        
    </div>
     
  )
}

export default FilmDetail
