  //     );
  //     setItems(response.data.results);
  //     setTotalPageNumber(response.data.total_pages);
  //   } catch (err) {
  //     console.error(err);
  //   }
  // };


  const dispatch = useDispatch();

  useEffect(() => {
    dispatch(getAllMovie())
  }, [option, language, page]);

  const dataMovies = useSelector((state)=>state.movies)
  setItems(dataMovies)

  // const getData = async () => {
