  //   }
  // };



  const linkImg = 'https://image.tmdb.org/t/p/w500';



  return (
    <>
      <BrowserRouter>
        <NavBar setItems={setItems} getData={getData} />
        <Routes>
          <Route
            path='/'
            element={
              <FilmList
                items={items}
                linkImg={linkImg}
                option={option}
                setOption={setOption}
                setLanguage={setLanguage}
                setPage={setPage}
                totalPageNumber={totalPageNumber}
              />
            }
          />
          <Route path='/movie/:id' element={<FilmDetail linkImg={linkImg} items={items} />} />
        </Routes>
      </BrowserRouter>
    </>
  );
}

export default App;
