import {AllMovies} from '../types/moviesType'

const initialValue = {movies : [] , pageeCount : 0}
// 2-Create reducer
export const moviesReducer = (state = initialValue , action)=>{
    switch(action.type){
        case AllMovies :
            return { movies : action.data }
            
        default :
            return {state}    
    }

}
