import React from 'react'
import ReactPaginate from 'react-paginate'
import './Pagination.css'
/**/import { useDispatch, useSelector } from 'react-redux'
/**/import { getAllMovie } from '../../redux/actions/movieAction.js'


const PaginationCompo = ({setPage , totalPageNumber }) => {


  





    const handelClick =  (select) => {
      const numPge = select.selected + 1;
      setPage(numPge) 
    }

    // const pageCount = totalPageNumber 
    const pageCount = 500 

  return (
    <div className='navigate'>
       <ReactPaginate
        breakLabel="..."
        nextLabel="next >"
        onPageChange={handelClick}
        marginPagesDisplayed={2}
        pageRangeDisplayed={3}
        pageCount={pageCount}
        previousLabel="< previous"
        renderOnZeroPageCount={null}
        containerClassName={'pagination justify-content-center p-3'}

        pageClassName={'page-item'}
        pageLinkClassName={'page-link'}

        previousClassName={'page-item'}
        previousLinkClassName={'page-link'}

        nextClassName={'page-item'}
        nextLinkClassName={'page-link'}

        breakClassName={'page-item'}
        breakLinkClassName={'page-link'}
        // active page
        activeClassName={'active selectedPage'}
      />
  </div>
  )
}

export default PaginationCompo
