import axios from 'axios'
import {AllMovies} from '../types/moviesType'


export const getAllMovie = async ()=>{
    const allMovie = await axios.get("https://api.themoviedb.org/3/discover/movie?api_key=d0479c49cb948d6ad0bb103813fe9e3c&language=an")
    return {type : AllMovies , data : allMovie.data , pages : 0}
}
