import {useEffect, useState} from 'react'
import './App.css';
import 'bootstrap/dist/css/bootstrap.min.css';
import NavBar from './components/navBar/navBar'
// import Films from './components/FilmsCard/Films'
import FilmList from './components/FilmList/FilmList'
import FilmDetail from './components/FilmDetail/FilmDetail'
import {Routes , Route , BrowserRouter} from 'react-router-dom'

import axios from 'axios'

function App() {

  const [ items , setItems ] = useState([])
  const [ option , setOption ] = useState('movie')
  const [ language , setLanguage ] = useState('an')
  const [ page , setPage ] = useState('1')
  const [ totalPageNumber , setTotalPageNumber ] = useState()


  const getData = async()=>{

    try{
     const response = await axios.get(`https://api.themoviedb.org/3/discover/${option}?api_key=d0479c49cb948d6ad0bb103813fe9e3c&language=${language}-US&page=${page}`)
              //  https://api.themoviedb.org/3/movie/popular?api_key=d0479c49cb948d6ad0bb103813fe9e3c&language=en-US&page1
              // `https://api.themoviedb.org/3/discover/movie?api_key=d0479c49cb948d6ad0bb103813fe9e3c&language=en-US&page1`
    setItems(response.data.results)
    setTotalPageNumber(response.data.total_pages)



    }catch(err){
      console.error(err)
    }
  }

  let linkImg = "https://image.tmdb.org/t/p/w500"

  useEffect(()=>{
    getData()

    console.log(items);

  }, [option , language , page])



  return (
    <>
      <NavBar setItems={setItems} getData={getData} />
      <BrowserRouter>
        <Routes>
          <Route path='/' element={<FilmList items={items} linkImg={linkImg} option={option} setOption={setOption} setLanguage={setLanguage} setPage={setPage} totalPageNumber={totalPageNumber} />
} />

          <Route path='/movie/?' element={<FilmDetail />} />


        </Routes>
      </BrowserRouter>  
    </>
  );
}

export default App;
