import React, { useState } from 'react'
import './navBar.css'
import axios from 'axios'



const NavBar = ({setItems , getData}) => {
  const [search , setSearch] = useState('')
  // const [newData , setNewData] = useState([])

  const handelSearch = (e)=>{
    const event = e.target.value;
    setSearch(event) 
    console.log(search);
    
  }

  const newDataFiltred = async()=>{
    try{
      if(search === "" ){
        getData()
      }else{
        const data = await axios.get(`https://api.themoviedb.org/3/search/movie?query=${search}&api_key=d0479c49cb948d6ad0bb103813fe9e3c`) 
        setItems(data.data.results)
        setSearch('')
      }
    }
    catch(err){
      console.error("rayen the error : " + err)
    }
  }

  return (
    <nav>
        <h5>
            Movie <span>   </span>
            <img src="https://icon-library.com/images/movies-icon-png/movies-icon-png-8.jpg" alt='logo'/>
        </h5>
        <div className="search" >
            <input className='searchInput' type="text" placeholder="search ..." value={search} onChange={handelSearch}/>
            <button className='' onClick={newDataFiltred}>Search</button>
        </div>
    </nav>
  )
}

export default NavBar
